import React, { Component } from 'react'
import './index.sass'
import CharacterInfoResult from '../CharacterInfoResult'
import SearchResults from '../SearchResults'
import logic from '../Logic'


export default class Home extends Component {

    state = { query: null, characters: null, feedback: null, character: null,}

    handleSearch = () => {
        try {
            logic.searchCharacter(this.state.query)
                .then(characters => {
                    const {results} = characters
                    this.setState({
                        feedback: null,
                        characters: results.map(({ id, name, thumbnail: {path,extension} }) => ({id, name, path, extension}))
                    })
                })
                .catch(({ message }) => this.setState({ feedback: message, characters: null }))
        } catch ({ message }) {
            this.setState({ feedback: message, characters: null })
        }
    }

    handleCharacterSelected = id => {
        try {
        logic.retrieveCharacter(id)
            .then(el => this.setState({character: el.results[0]}))
            .catch(({ message }) => {
                this.setState({character: null})
                this.setState({message}) 
            })
        } catch ({ message }) {
            this.setState({character: null})
            this.setState({message}) 
        }
    }

    render() {

        const { handleSearch, handleCharacterSelected, handleComicSelected, state: {characters, character, feedback}, props: { searchBtn, searchInput, moreInfo, seeComics, price, comics }} = this

        return <section className="margin-top">
        <div className="columns is-mobile is-centered">
            <div className="column is-two-thirds-tablet is-three-quarters-mobile is-centered"> 
                <form onSubmit={(e) => {e.preventDefault(); handleSearch()}} className="field has-addons has-addons-centered">
                    <div className="control has-icons-left is-expanded">
                        <input onChange={e => this.setState({query: e.target.value})} className="input is-small is-rounded" placeholder={searchInput} type="text" name="query"></input>
                        <span className="icon is-small is-left">
                            <i className="fas fa-bolt"></i>
                        </span>
                    </div>
                    <div className="control">
                        <button className="button is-small is-rounded is-warning"type="submit">{searchBtn}</button>
                    </div>
                </form>
            </div>
        </div>
        {(characters || feedback) && <SearchResults characters={characters} feedback={feedback} onCharacterSelected={handleCharacterSelected} />}
        {(character || feedback) && <CharacterInfoResult character={character} feedback={feedback} onComicSelected={handleComicSelected} moreInfo={moreInfo} seeComics={seeComics} comics={comics}/> }   
    </section>
    }
}
