import React, { Component } from 'react'

export default class Counter extends Component {

  state = {count: 0, loading: false}

  handleAdd = () => {this.setState({count: this.state.count + 1}); this.setState({loading: false})}
  handleSubstract = () => {this.setState({count: this.state.count - 1}); this.setState({loading: false})}
  handelReset = () => {this.setState({count: 0}); this.setState({loading: false})}
  handleLoading = () => this.setState({loading: !this.state.loading})

  render() {
    const {props: {handleAdd, handleLoading, handelReset, handleSubstract}} = this
    return (
      <div>
        <button onClick={() => {
          handleLoading();
          setTimeout(handleSubstract(), 1000)
          }}>-</button>
        <span>{loading ? <span><i className=" favourite__loading fas fa-spinner fa-spin fa-1x" /> </span>: count}</span>
        <button onClick={() => {
          handleLoading();
          setTimeout(handleAdd(), 1000)
          }}>+</button>
        <br />
        <button onClick={() => {
          handleLoading();
          setTimeout(handelReset(), 1000)
        }}>Reset</button>
      </div>
    )
  }


}
