import React, { Component } from 'react';
import './index.sass';
import CharacterInfoResult from '../CharacterInfoResult'
// import ComicInfoResult from '../ComicInfoResult'
import logic from '../Logic'
import MultipleResults from '../MultipleResults';


class Home extends Component {

    state = {characterId: null, comicId: null, query: null, characters: null, feedback: null}

    handleQueryInput = ({ event: { target: value } }) => this.setState({ query });

    handleSearch = query => {
        try {
            logic.searchCharacter({ target: { value: query } })
                .then(characters => {
                    const {results} = characters
                    this.setState({
                        feedback: null,
                        characters: results.map(({ id, name, thumbnail: {path,extension} }) => ({id, name, path, extension}))
                    })
                })
                .catch(({ message }) => this.setState({ feedback: message, characters: null }))
        } catch ({ message }) {
            this.setState({ feedback: message, characters: null })
        }
    }

    handleCharacterSelected = id => {
        // this.setState({characterId: id}, () => this.props.history.push(`/home/search/character/${id}`))
        this.setState({characterId : id})
    }

    handleComicSelected = id => {
        this.setState({comicId: id})
    }


    render() {

        const { handleQueryInput, handleSearch, handleCharacterSelected, handleComicSelected, characterId, characters, feedback, props: { searchBtn, searchInput, moreInfo, seeComics, price, comics }} = this

        return <section className="margin-top">
        <div className="columns is-mobile is-centered">
            <div className="column is-two-thirds-tablet is-three-quarters-mobile is-centered"> 
                <form onSubmit={handleSearch} className="field has-addons has-addons-centered">
                    <div className="control has-icons-left is-expanded">
                        <input onChange={handleQueryInput} className="input is-small is-rounded" placeholder={searchInput} type="text" name="query"></input>
                        <span className="icon is-small is-left">
                            <i className="fas fa-bolt"></i>
                        </span>
                    </div>
                    <div className="control">
                        <button className="button is-small is-rounded is-warning"type="submit">{searchBtn}</button>
                    </div>
                </form>
            </div>
        </div>
        {characters && <MultipleResults characters={characters} feedback={feedback} onCharacterSelected={handleCharacterSelected} />}
        {characterId && <CharacterInfoResult id={characterId} onComicSelected={handleComicSelected} moreInfo={moreInfo} seeComics={seeComics} comics={comics}/> }   
            {/* <Route  path="/home/search/comic/:id" render={props => <ComicInfoResult id={props.match.params.id} onCharacterSelected={handleCharacterSelected} moreInfo={moreInfo} price={price}/>} /> */}
    </section>
    }
}

export default Home;
