import React, { Component } from 'react'

export default class Counter extends Component {

  state = {count: 0, loading: false}

  handleAdd = () => {this.setState(count + 1); this.setState(loading= false)}
  handleSubstract = () => {this.setState(count - 1); this.setState(loading= false)}
  handelReset = () => {this.setState(count = 0); this.setState(loading= false)}
  handleLoading = () => this.setState(loading= !loading)

  render() {
    return 
      <div>
        <button onClick={() => {
          setCount('loading' ) 
          setTimeout(function() {setCount('substract')}, 1000)
          }}>-</button>
        <span>{loading ? <span><i className=" favourite__loading fas fa-spinner fa-spin fa-1x" /> </span>: count}</span>
        <button onClick={() => {
          setCount('loading') 
          setTimeout(function() {setCount('add')}, 1000)
          }}>+</button>
        <br />
        <button onClick={() => {
          setCount('loading') 
          setTimeout(function() {setCount('reset')}, 1000)
        }}>Reset</button>
      </div>
  }


}
