import React, { Component } from 'react'
import Login from '../Login'
import Home from '../Home'
import logic from '../Logic'
import LanguageSelector from '../LanguageSelector'
import i18n from '../../i18n'

class App extends Component {

  state = {selectedLanguage: 'en'}

  handleLanguageSelected = event => {
    this.setState({
        selectedLanguage: event.target.value
    })
  }

  handleLogOut = () => {
    logic.logout()
    this.props.history.push('/')
  }

  componentWillMount() {
    this.props.history.listen(() => {
    if(this.props.location.pathname === '/' && logic.userLoggedIn) this.props.history.push('/home')
    });
  }

  render() {
    const { handleLogOut, handleLanguageSelected, state:{selectedLanguage}}= this

    return <main className="App">
        <LanguageSelector selectedLanguage={selectedLanguage} languages={['en', 'ch', 'fr', 'ca', 'es']} onLanguageSelected={handleLanguageSelected} />
      <h1 className="title">React Hooks Skylab Demo</h1>
        <div className="exercises">
          <div className="exercise__unit">
            <div className="exercise__unit-title">Form</div>
            <div className="exercise__unit-content">
              <Login modal__feedback= {i18n[selectedLanguage].modal__feedback}login={i18n[selectedLanguage].login} register={i18n[selectedLanguage].register} welcome__title={i18n[selectedLanguage].welcome__title} welcome__subtitle={i18n[selectedLanguage].welcome__subtitle} goBack={i18n[selectedLanguage].goBack} email={i18n[selectedLanguage].email} password={i18n[selectedLanguage].password} name={i18n[selectedLanguage].name} surname={i18n[selectedLanguage].surname} confirmPassword={i18n[selectedLanguage].confirmPassword}   />}
            </div>
          </div>
          <div className="exercise__unit">
            <div className="exercise__unit-title">Props</div>
            <div className="exercise__unit-content">
            <Home onLogout={handleLogOut} home={i18n[selectedLanguage].home} favourites={i18n[selectedLanguage].favourites} youtube={i18n[selectedLanguage].youtube} logOut={i18n[selectedLanguage].logOut} searchBtn={i18n[selectedLanguage].searchBtn} searchInput={i18n[selectedLanguage].searchInput} topCharacters={i18n[selectedLanguage].topCharacters} topComics={i18n[selectedLanguage].topComics} favs__feedback={i18n[selectedLanguage].favs__feedback} favs__title={i18n[selectedLanguage].favs__title} seeComics={i18n[selectedLanguage].seeComics} moreInfo={i18n[selectedLanguage].moreInfo} price={i18n[selectedLanguage].price} characters={i18n[selectedLanguage].characters} comics={i18n[selectedLanguage].comics}/>}
            </div>
          </div>
        </div>
      </main>  
  }
}

export default App
